import React from "react";
//Paper creates a paper like view with few efects we make it like 3D view
import{Paper, Typography} from "@material-ui/core";


const VideoDetail = (props) => {
    const {video} = props;
    if(!video) return <div>Loading...</div>

    //Below variable is used to fetch the src of the video
    const videoSrc = `https://www.youtube.com/embed/${video.id.videoId}`
    return (
        <React.Fragment>
            <Paper elevation={6} style={{height: '70%'}}>
                {/* Iframe is used to play video */}
                <iframe frameBorder="0" height="100%" width="100%" title="Video Player" src="..."/>

            </Paper>
            <Paper elevation={6} style={{padding: '15px'}}>

            </Paper>
        </React.Fragment>
    )
}

export default VideoDetail;
